{"changed":true,"filter":false,"title":"sessions_controller.rb","tooltip":"/app/controllers/sessions_controller.rb","value":"class SessionsController < ApplicationController\n  before_action :set_session, only: [:show, :edit, :update, :destroy, :new_reservation, :create_reservation]\n\n  # GET /sessions\n  # GET /sessions.json\n  def index\n    @sessions = Session.all\n  end\n\n  # GET /sessions/1\n  # GET /sessions/1.json\n  def show\n  end\n\n  # GET /sessions/new\n  def new\n    @session = Session.new\n  end\n\n  # GET /sessions/1/edit\n  def edit\n  end\n\n  # POST /sessions\n  # POST /sessions.json\n  def create\n    @session = Session.new(session_params)\n\n    respond_to do |format|\n      if @session.save\n        format.html { redirect_to @session, notice: 'Session was successfully created.' }\n        format.json { render :show, status: :created, location: @session }\n      else\n        format.html { render :new }\n        format.json { render json: @session.errors, status: :unprocessable_entity }\n      end\n    end\n  end\n\n  # PATCH/PUT /sessions/1\n  # PATCH/PUT /sessions/1.json\n  def update\n    respond_to do |format|\n      if @session.update(session_params)\n        format.html { redirect_to @session, notice: 'Session was successfully updated.' }\n        format.json { render :show, status: :ok, location: @session }\n      else\n        format.html { render :edit }\n        format.json { render json: @session.errors, status: :unprocessable_entity }\n      end\n    end\n  end\n  \n  def home\n    render 'sessions/home'\n  end\n  \n  def email\n    render 'sessions/email_search'\n  end\n  \n  def filter\n    date = params[:date]\n    @sessions = Session.where(date: date)\n\n    respond_to do |format|\n      format.json { render json: @sessions }\n    end\n  end\n  \n  def email_search\n      user = User.where(email: params[:email]).first\n      if user != nil\n        @sessions = user.sessions.where(\"date >= ?\", params[:date])\n      else\n        @session = []\n      end\n      \n      respond_to do |format|\n        format.json { render json: @sessions }\n      end\n  end\n  \n  def new_reservation\n    this_session = Session.find(params[:id])\n    @date = this_session.date\n    @time = this_session.time\n    \n    render 'sessions/new_reservation'\n  end\n  \n  def create_reservation\n    user = User.find_or_initialize_by(email: params[:email])\n    user.first = params[:first]\n    user.last = params[:last]\n    user.save\n    \n    reservation_check = Reservation.where(user_id: user.id, session_id: @session.id).first\n    \n    if reservation_check == nil\n      reservation = Reservation.new(user_id: user.id, session_id: @session.id)\n      reservation.save\n    \n      capacity = @session.current_capacity\n      capacity += 1\n      @session.update(current_capacity: capacity)\n      \n      @success = true;\n      \n      render 'sessions/home'\n    else\n      @error = 'This email is already registered for this session'\n      \n      @date = @session.date\n      @time = @session.time\n      \n      render 'sessions/new_reservation'\n    end\n    \n  end\n\n  # DELETE /sessions/1\n  # DELETE /sessions/1.json\n  def destroy\n    @session.destroy\n    respond_to do |format|\n      format.html { redirect_to sessions_url, notice: 'Session was successfully destroyed.' }\n      format.json { head :no_content }\n    end\n  end\n\n  private\n    # Use callbacks to share common setup or constraints between actions.\n    def set_session\n      @session = Session.find(params[:id])\n    end\n\n    # Never trust parameters from the scary internet, only allow the white list through.\n    def session_params\n      params.require(:session).permit(:date, :time, :current_capacity, :max_capacity)\n    end\nend\n","undoManager":{"stack":[[{"start":{"row":92,"column":15},"end":{"row":92,"column":16},"action":"insert","lines":["_"],"id":911}],[{"start":{"row":92,"column":16},"end":{"row":92,"column":17},"action":"insert","